mapper.insert:

    SELECT LAST_INSERT_ID() AS community_id

INSERT INTO Community(user_id, community_type, title, text)
VALUES (#{userId}, #{communityType}, #{title}, #{text});


    SELECT LAST_INSERT_ID() AS community_image_id

INSERT INTO Community_Image_Path(imagePath)
VALUES (#{imagePath});

mapper.select:
SELECT c.community_id, c.user_id, (SELECT nickname FROM User WHERE user_id = c.user_id) AS nickname, c.community_type, c.title, c.text, c.regdate, (SELECT COUNT(*) FROM Favorite_Community WHERE favorite_type_id = c.community_id) AS favorite_count, (SELECT COUNT(*) FROM Comment WHERE community_id = c.community_id) AS comment_count FROM Community c LEFT JOIN Favorite_Community fc ON c.community_id = fc.favorite_type_id AND fc.favorite_type = '1' -- LEFT JOIN Comment cm ON c.community_id = cm.community_id WHERE c.community_type = #{status}
AND (c.title LIKE CONCAT('%', #{search}, '%') OR c.text LIKE CONCAT('%', #{search}, '%'))
AND c.title LIKE CONCAT('%', #{search}, '%')
AND c.text LIKE CONCAT('%', #{search}, '%')
ORDER BY c.community_id DESC LIMIT #{startIndex}, #{rowPerPage}

mapper.update:
update Community_Image_Path
SET imagePath = #{fileName}
WHERE community_image_id = #{imageId};

mapper.select:
SELECT imagePath
FROM Community_Image_Path
WHERE community_image_id = #{removeImageId}
