mapper.select:
SELECT user_id
FROM User
WHERE user_name = #{username}

SELECT name
FROM User
WHERE user_name = #{username}

SELECT count(*)
FROM User
WHERE user_name = #{username}

mapper.update:
UPDATE User
SET    name = #{name},
       nickname = #{nickname},
       email = #{email},
       introduce = #{introduce}
WHERE user_name = #{userName}

UPDATE User
SET    profile_picture = #{file}
WHERE user_name = #{userName}
